import React, { useState, useEffect } from 'react';
import '../css/LoginForm.css'
import { Link } from 'react-router-dom';  

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  useEffect(() => {
    const savedEmail = localStorage.getItem('rememberedEmail');
    if (savedEmail) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Email:', email);
    console.log('Password:', password);

    if (rememberMe) {
      localStorage.setItem('rememberedEmail', email);
    } else {
      localStorage.removeItem('rememberedEmail');
    }
  };

  const handleCheckboxChange = (e) => {
    setRememberMe(e.target.checked);
  };

  return (
    <div className='login-background'>
      <div className='login-form'>
       <img src={require('../image/gunlogo.png')} alt="로고" width="150px"/>
        <h1 className='login-main'>로그인</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className='login-id'
              placeholder='아이디를 입력해주세요'
              required
            />
          </div>
          <div>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className='login-pw'
              placeholder='비밀번호를 입력해주세요'
              required
            />
          </div>
          <div>
            <label className='logincheck'>
              <input
                type="checkbox"
                checked={rememberMe}
                onChange={handleCheckboxChange}
              />
              아이디저장
            </label>
            <Link to="/search" className='login-search'>아이디/비밀번호 찾기</Link> 
          </div>
          <button type="submit" className='login-login'>로그인</button>
        </form>
        <Link to="/join" className='login-join'>회원가입</Link> 
      </div>
    </div>
  );
};

export default LoginForm;
