import React, { useState } from 'react';
import '../css/StarRating.css'; // 스타일을 불러옴

const StarRating = ({ rating, onRatingChange }) => {
  const [hover, setHover] = useState(null);

  return (
    <div className="star-rating">
      {[...Array(5)].map((star, index) => {
        const currentRating = index + 1;
        return (
          <button
            type="button"
            key={index}
            className={currentRating <= (hover || rating) ? "on" : "off"} // 조건에 맞는 클래스 적용
            onClick={() => onRatingChange(currentRating)}  // 클릭한 별점에 따라 색상 결정
            onMouseEnter={() => setHover(currentRating)}    // 별 위에 마우스를 올렸을 때의 상태
            onMouseLeave={() => setHover(null)}             // 마우스를 떼면 원래 상태로 돌아옴
            style={{ cursor: 'pointer', border: 'none', backgroundColor: 'transparent' }}
          >
            <span className="star">&#9733;</span> {/* Unicode for star */}
          </button>
        );
      })}
    </div>
  );
};

export default StarRating;
