import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Detail(props) {
    let { id } = useParams();  // URL에서 id 값을 가져옴
    let 찾은상품 = props.MG.find(x => x.id == id);  // 해당 id의 상품을 찾음
    
    useEffect(() => {
        let a = setTimeout(() => {
            setAlert(false);
        }, 2000);
        return () => {
            clearTimeout(a);
        };
    }, []);

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    {/* 동적으로 이미지 경로를 설정 */}
                    <img src={process.env.PUBLIC_URL + `/images/mg${id}.jpg`} alt={찾은상품.gundam_name} width="100%" />
                </div>
                <div className="col-md-6">
                    <h4 className="pt-5">{찾은상품.gundam_series}</h4>
                    <p>{찾은상품.gundam_name}</p>
                    <p>{찾은상품.price}</p>
                    <button className="btn btn-danger">주문하기</button>
                </div>
            </div>
        </div>
    );
}

export default Detail;
