import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../css/Checkout.css';

function Checkout() {
  // useLocation으로 Detail 페이지에서 넘어온 상태를 받음
  const location = useLocation();
  const { productName, productImage, productPrice, quantity } = location.state;
  const totalPrice = productPrice * quantity;

  // 배송지 정보 상태
  const [name, setName] = useState('');
  const [phone, setPhone] = useState({ first: '', second: '', third: '' });
  const [email, setEmail] = useState('');
  const [receiverName, setReceiverName] = useState('');
  const [address, setAddress] = useState('');
  const [postCode, setPostCode] = useState('');
  const [generalPhone, setGeneralPhone] = useState({ first: '', second: '', third: '' });
  const [mobilePhone, setMobilePhone] = useState({ first: '', second: '', third: '' });
  const [memo, setMemo] = useState('');
  const [packagingRequest, setPackagingRequest] = useState('');
  const [addToAddressList, setAddToAddressList] = useState(false);
  const [coupon, setCoupon] = useState('');

  const handlePhoneChange = (type, value) => {
    setPhone({ ...phone, [type]: value });
  };

  const handleGeneralPhoneChange = (type, value) => {
    setGeneralPhone({ ...generalPhone, [type]: value });
  };

  const handleMobilePhoneChange = (type, value) => {
    setMobilePhone({ ...mobilePhone, [type]: value });
  };

  return (
    <div className="checkout-container">
      {/* Step bar */}
      <div className="checkout-steps">
        <div className="step">STEP 1<br />장바구니</div>
        <div className="step active">STEP 2<br />주문/결제</div>
        <div className="step">STEP 3<br />주문완료</div>
      </div>

      {/* 주문리스트 섹션 */}
      <div className="order-list-section">
        <h3>주문리스트</h3>
        <div className="order-item">
          <img src={productImage} alt="상품 이미지" />
          <div className="order-details">
            <p>상품명: {productName}</p>
            <p>수량: {quantity}개</p>
            <p>상품 금액: {productPrice}원</p>
          </div>
        </div>

        <div className="summary">
          <p>상품 총 금액: {totalPrice}원</p>
          <p>총 배송비: 3,000원</p>
          <p>결제 예정 금액: {totalPrice + 3000}원</p>
        </div>
      </div>

      {/* 배송지 정보 입력 섹션 */}
      <div className="address-section">
        <h3>1. 배송지 정보 입력</h3>

        <div className="form-group">
          <label>주문자 성함</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="주문자 성함을 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>주문자 연락처</label>
          <div className="phone-input">
            <input
              type="text"
              value={phone.first}
              onChange={(e) => handlePhoneChange('first', e.target.value)}
              maxLength="3"
            />
            <span>-</span>
            <input
              type="text"
              value={phone.second}
              onChange={(e) => handlePhoneChange('second', e.target.value)}
              maxLength="4"
            />
            <span>-</span>
            <input
              type="text"
              value={phone.third}
              onChange={(e) => handlePhoneChange('third', e.target.value)}
              maxLength="4"
            />
          </div>
        </div>

        <div className="form-group">
          <label>주문자 이메일</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="주문자 이메일을 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>받으시는 분</label>
          <input
            type="text"
            value={receiverName}
            onChange={(e) => setReceiverName(e.target.value)}
            placeholder="받으시는 분 성함을 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>받으실 주소</label>
          <input
            type="text"
            value={postCode}
            onChange={(e) => setPostCode(e.target.value)}
            placeholder="우편번호"
          />
          <button type="button" className="btn btn-light">
            우편번호 찾기
          </button>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="상세 주소를 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>수령인 일반전화</label>
          <div className="phone-input">
            <input
              type="text"
              value={generalPhone.first}
              onChange={(e) => handleGeneralPhoneChange('first', e.target.value)}
              maxLength="3"
            />
            <span>-</span>
            <input
              type="text"
              value={generalPhone.second}
              onChange={(e) => handleGeneralPhoneChange('second', e.target.value)}
              maxLength="4"
            />
            <span>-</span>
            <input
              type="text"
              value={generalPhone.third}
              onChange={(e) => handleGeneralPhoneChange('third', e.target.value)}
              maxLength="4"
            />
          </div>
        </div>

        <div className="form-group">
          <label>수령인 휴대전화</label>
          <div className="phone-input">
            <input
              type="text"
              value={mobilePhone.first}
              onChange={(e) => handleMobilePhoneChange('first', e.target.value)}
              maxLength="3"
            />
            <span>-</span>
            <input
              type="text"
              value={mobilePhone.second}
              onChange={(e) => handleMobilePhoneChange('second', e.target.value)}
              maxLength="4"
            />
            <span>-</span>
            <input
              type="text"
              value={mobilePhone.third}
              onChange={(e) => handleMobilePhoneChange('third', e.target.value)}
              maxLength="4"
            />
          </div>
        </div>

        <div className="form-group">
          <label>배송 시 메모</label>
          <input
            type="text"
            value={memo}
            onChange={(e) => setMemo(e.target.value)}
            placeholder="배송 시 요청 사항을 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>포장 시 요구사항</label>
          <input
            type="text"
            value={packagingRequest}
            onChange={(e) => setPackagingRequest(e.target.value)}
            placeholder="포장 시 요구사항을 입력하세요"
          />
        </div>

        <div className="form-group">
          <label>
            <input
              type="checkbox"
              checked={addToAddressList}
              onChange={(e) => setAddToAddressList(e.target.checked)}
            />
            배송지 목록에 추가
          </label>
        </div>
      </div>

      {/* 할인 내역 입력 섹션 */}
      <div className="discount-section">
        <h3>할인 내역 입력</h3>
        <label>쿠폰</label>
        <input
          type="text"
          value={coupon}
          onChange={(e) => setCoupon(e.target.value)}
        />
        <p>적립금: 0원</p>
      </div>

      {/* 결제 정보 섹션 */}
      <div className="payment-section">
        <h3>결제 정보</h3>
        <p>상품 금액: {productPrice * quantity}원</p>
        <p>배송비: 3,000원</p>
        <h4>총 결제 금액: {totalPrice + 3000}원</h4>
        <button className="btn btn-primary">결제하기</button>
      </div>
    </div>
  );
}

export default Checkout;
