import { useParams } from "react-router-dom";
import '../css/Detail.css';

function Detail(props) {
    let { id } = useParams();  // URL에서 id 값을 가져옴
    let 찾은상품 = props.MG.find(x => x.id == id);  // 해당 id의 상품을 찾음

    return (
        <div className="container detail-container">
            <div className="row">
                {/* 좌측 이미지 영역 */}
                <div className="col-md-6">
                    <img src={process.env.PUBLIC_URL + `/images/mg${id}.jpg`} alt={찾은상품.gundam_name} width="100%" />
                    {/* 썸네일 이미지도 필요시 추가 가능 */}
                    <div className="thumbnail-images">
                        <img src={process.env.PUBLIC_URL + `/images/mg${id}.jpg`} alt="thumbnail" width="50px" />
                        {/* 추가 썸네일 이미지들 */}
                    </div>
                </div>

                {/* 우측 상세 정보 영역 */}
                <div className="col-md-6">
                    <h2>{찾은상품.gundam_name}</h2>
                    <p>{찾은상품.price}</p>

                    {/* 가격과 주문 관련 정보 */}
                    <div className="product-info">
                        <p><strong>시리즈명: </strong>{찾은상품.gundam_series}</p>
                        <p><strong>수령 방법: </strong>택배 수령</p>
                    </div>

                    {/* 수량 선택 */}
                    <div className="quantity-selector">
                        <button className="btn btn-outline-secondary">-</button>
                        <span className="quantity">1</span>
                        <button className="btn btn-outline-secondary">+</button>
                    </div>

                    {/* 총 가격 */}
                    <p><strong>총 상품금액: </strong>{찾은상품.price}</p>

                    {/* 구매/장바구니/선물하기 버튼 */}
                    <div className="actions">
                        <button className="btn btn-danger">구매하기</button>
                        <button className="btn btn-outline-dark">장바구니</button>
                        <button className="btn btn-outline-dark">선물하기</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Detail;
