import StarRating from './StarRating'; // 파일 경로를 맞게 설정

function Detail(props) {
    let { id } = useParams();
    let 찾은상품 = props.MG.find(x => x.id == id);

    // 수량을 관리하는 상태
    let [quantity, setQuantity] = useState(1);

    // 리뷰와 별점을 관리하는 상태
    let [reviews, setReviews] = useState([
        { user: "사용자1", content: "정말 멋진 프라모델입니다!", rating: 5 },
        { user: "사용자2", content: "완성도 높은 제품입니다.", rating: 4 }
    ]);
    let [newReview, setNewReview] = useState("");
    let [newRating, setNewRating] = useState(0); // 새 리뷰에 대한 별점

    // 리뷰 추가 함수
    const addReview = () => {
        if (newReview) {
            setReviews([...reviews, { user: "익명 사용자", content: newReview, rating: newRating }]);
            setNewReview("");  // 입력 필드를 비움
            setNewRating(0);   // 별점 초기화
        }
    };

    return (
        <div className="container detail-container" style={{ marginTop: '50px' }}>
            {/* 상품 상세보기 코드 생략 */}

            <div className="product-tabs mt-5">
                <Tabs defaultActiveKey="detail" id="product-tabs" className="mb-3">
                    <Tab eventKey="detail" title="상세보기">
                        {/* 상세 이미지 및 설명 */}
                    </Tab>

                    <Tab eventKey="reviews" title="상품리뷰">
                        <div>
                            {/* 리뷰 리스트 */}
                            {reviews.map((review, index) => (
                                <div key={index} className="review">
                                    <p><strong>{review.user}</strong>: {review.content}</p>
                                    <p>평점: {review.rating} / 5</p>
                                </div>
                            ))}

                            {/* 리뷰 작성 및 별점 */}
                            <div className="mt-4">
                                <h5>리뷰 작성하기</h5>
                                <StarRating rating={newRating} onRatingChange={setNewRating} />
                                <textarea
                                    className="form-control"
                                    rows="3"
                                    value={newReview}
                                    onChange={(e) => setNewReview(e.target.value)}
                                    placeholder="리뷰를 작성하세요..."
                                />
                                <button className="btn btn-primary mt-3" onClick={addReview}>리뷰 추가</button>
                            </div>
                        </div>
                    </Tab>
                </Tabs>
            </div>
        </div>
    );
}

export default Detail;
