import { useState } from "react";
import { Navbar, Container, Nav, Dropdown, Carousel } from 'react-bootstrap';
import './App.css';
import mg from './pages/data.js';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Detail from "./pages/Detail.js";
import Login from "./pages/Login.js";
import MemberSearch from './pages/Member_search';
import MemberJoin from './pages/Member_Join';
import Footer from './pages/Footer';
import AdminProductForm from './pages/AdminProductForm';
import AdminPage from './pages/AdminPage';

function App() {
  const [MG, setMG] = useState(mg); // 상품 데이터를 상태로 관리
  let navigate = useNavigate();

  // Dropdown의 hover 상태를 관리하는 state
  const [showDropdown, setShowDropdown] = useState(false);

  // 상품 추가 함수 (관리자 페이지에서 호출됨)
  const handleAddProduct = (newProduct) => {
    setMG([...MG, newProduct]);
  };

  const handleMouseEnter = () => {
    setShowDropdown(true);
  };

  const handleMouseLeave = () => {
    setShowDropdown(false);
  };

  return (
    <div className="App">
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container fluid>
          <Navbar.Brand onClick={() => { navigate('/') }} style={{ cursor: 'pointer' }}>
            GundShop
          </Navbar.Brand>
          <Nav className="me-auto">
            <Dropdown
              show={showDropdown}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <Dropdown.Toggle variant="dark" id="dropdown-basic">
                건담 프라모델 . 기타
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item onClick={() => { navigate('/adminproductForm') }}>건담 프라모델</Dropdown.Item>
                <Dropdown.Item onClick={() => { navigate('/adminpage') }}>기타 프라모델</Dropdown.Item>
                <Dropdown.Item onClick={() => { navigate('/tools') }}>조립 도구</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Nav>

          <Nav className="ms-auto">
            <Nav.Link onClick={() => { navigate('/login') }}>로그인</Nav.Link>
            <Nav.Link onClick={() => { navigate('/join') }}>회원가입</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Routes>
        {/* 메인 페이지 */}
        <Route path="/" element={
          <>
            <Carousel className="main-bg">
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src={process.env.PUBLIC_URL + '/images/bg.jpg'}
                  alt="Slide 1"
                />
                <Carousel.Caption style={{ fontSize: '38px', fontWeight: 'bold' }}>
                  건담 X 신한카드 이벤트 바로가기
                </Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src={process.env.PUBLIC_URL + '/images/bnr_top_pc.jpg'}
                  alt="Slide 2"
                />
                <Carousel.Caption></Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <img
                  className="d-block w-100"
                  src={process.env.PUBLIC_URL + '/images/seedfree.jpg'}
                  alt="Slide 3"
                />
                <Carousel.Caption style={{ fontWeight: 'bold' }}>
                  <p style={{ fontSize: '15px' }}>기동전사 건담</p>
                  <h2 style={{ fontSize: '38px' }}>SEED FREEDOM!</h2>
                </Carousel.Caption>
              </Carousel.Item>
            </Carousel>

            {/* 상품 목록 */}
            <div className="container">
              <div className="row">
                {MG.map((a, i) => (
                  <Card MG={MG[i]} i={i} key={i} navigate={navigate}></Card>
                ))}
              </div>
            </div>
          </>
        } />
        {/* 상품 상세 페이지 */}
        <Route path="/detail/:id" element={<Detail MG={MG} />} />
        {/* 로그인 페이지 */}
        <Route path="/login" element={<Login />} />
        {/* 회원 검색 페이지 */}
        <Route path="/search" element={<MemberSearch />} />
        {/* 회원 가입 페이지 */}
        <Route path="/join" element={<MemberJoin />} />
        {/* 관리자 상품 추가 폼 페이지 */}
        <Route path="/adminproductForm" element={<AdminProductForm onAddProduct={handleAddProduct} />} />
        {/* 관리자 페이지 (상품 목록) */}
        <Route path="/adminpage" element={<AdminPage products={MG} />} />
        {/* Footer */}
        <Route path="/footer" element={<Footer />} />
        <Route path="*" element={<div>없는 페이지 입니다.</div>} />
      </Routes>

      <Footer />
    </div>
  );
}

// 상품 카드를 표시하는 컴포넌트
function Card(props) {
  return (
    <div className="col-md-4" onClick={() => { props.navigate(`/detail/${props.MG.id}`) }} style={{ cursor: 'pointer' }}>
      <img src={process.env.PUBLIC_URL + `/images/mg${props.MG.id}.jpg`} alt="Gundam Model" width="100%" />
      <p>{props.MG.gundam_series}</p>
      <h5>{props.MG.gundam_name}</h5>
      <h4>{props.MG.price}</h4>
    </div>
  );
}

export default App;
