import { useState } from "react";

function AdminProductForm({ onAddProduct }) {
  const [productName, setProductName] = useState("");
  const [productSeries, setProductSeries] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productDetailImages, setProductDetailImages] = useState([]);

  // 상세 이미지를 처리하는 함수
  const handleDetailImagesChange = (e) => {
    const files = Array.from(e.target.files);
    const imageUrls = files.map(file => URL.createObjectURL(file));
    setProductDetailImages(imageUrls);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!productName || !productPrice || !productDetailImages.length) {
      alert("모든 필드를 입력해 주세요.");
      return;
    }

    const newProduct = {
      id: Date.now(),  // 고유 ID 생성
      gundam_name: productName,
      gundam_series: productSeries,
      price: productPrice,
      description: productDescription,
      detail_images: productDetailImages
    };

    onAddProduct(newProduct);  // 부모 컴포넌트로 상품 정보 전달
    resetForm();
  };

  const resetForm = () => {
    setProductName("");
    setProductSeries("");
    setProductPrice("");
    setProductDescription("");
    setProductDetailImages([]);
  };

  return (
    <div className="admin-product-form">
      <h2>상품 추가</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>상품 이름</label>
          <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} />
        </div>
        <div className="form-group">
          <label>상품 시리즈</label>
          <input type="text" value={productSeries} onChange={(e) => setProductSeries(e.target.value)} />
        </div>
        <div className="form-group">
          <label>상품 가격</label>
          <input type="text" value={productPrice} onChange={(e) => setProductPrice(e.target.value)} />
        </div>
        <div className="form-group">
          <label>상품 설명</label>
          <textarea value={productDescription} onChange={(e) => setProductDescription(e.target.value)} />
        </div>
        <div className="form-group">
          <label>상품 상세 이미지</label>
          <input type="file" multiple onChange={handleDetailImagesChange} />
        </div>
        <button type="submit">상품 추가</button>
      </form>
    </div>
  );
}

export default AdminProductForm;
