import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Detail.css'; // 스타일시트 포함

function Detail({ MG }) {
  let { id } = useParams();
  let 찾은상품 = MG.find(x => x.id === parseInt(id));
  let [alert, setAlert] = useState(true);

  useEffect(() => {
    let timer = setTimeout(() => { setAlert(false) }, 2000);
    return () => {
      clearTimeout(timer);
    }
  }, []);

  return (
    <div className="container mt-5">
      {/* 상단 탭 영역 */}
      <div className="sticky-tabs">
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <a className="nav-link active" href="#details">상세보기</a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#reviews">상품리뷰(83)</a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#qna">Q&A</a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#shipping">배송/교환/반품</a>
          </li>
        </ul>
      </div>

      <div className="row mt-3">
        {/* 왼쪽: 상품 이미지 및 설명 */}
        <div className="col-md-8">
          <div id="details">
            <img src={process.env.PUBLIC_URL + 찾은상품.image} alt={찾은상품.gundam_name} className="img-fluid" />
            <p>{찾은상품.gundam_series}</p>
            <p>{찾은상품.gundam_name}</p>
            <p>{찾은상품.price}</p>
          </div>
        </div>

        {/* 오른쪽: 상품 가격 및 주문 */}
        <div className="col-md-4">
          <div className="sticky-info">
            <h4 className="pt-5">{찾은상품.gundam_series}</h4>
            <p className="price">{찾은상품.price}</p>
            <button className="btn btn-danger">주문하기</button>
            <button className="btn btn-outline-secondary mt-2">장바구니에 추가</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Detail;
