import React, { useState } from 'react';
import '../css/Member_Join.css';

const MemberJoin = () => { 
    const [name, setName] = useState(''); // 이름
    const [birth, setBirth] = useState(''); // 생년월일
    const [id, setId] = useState(''); // 아이디
    const [password, setPassword] = useState(''); // 비밀번호
    const [mp, setMp] = useState(''); // 핸드폰번호
    const [hp, setHp] = useState(''); // 집번호
    const [hn, setHn] = useState(''); // 주소
    const [email, setEmail] = useState(''); // 메일 주소
    const [agreed, setAgreed] = useState(false); // 약관동의
    const [personalInfoAgreed, setPersonalInfoAgreed] = useState(false); // 개인정보 동의

    const handleJoinSubmit = (e) => {
        e.preventDefault();
        if (!agreed || !personalInfoAgreed) {
            alert("Please agree to the terms and conditions.");
            return;
        }
        // Proceed with form submission
        console.log({
            name,
            birth,
            id,
            password,
            mp,
            hp,
            hn,
            email
        });
    };

    return (
        <div className='join-background'>
            <div className='join-container'>
                <div className='join-header'>
                    <img src={require('../image/gunlogo.png')} alt="로고" className='search-logo'/>
                    <div style={{ fontSize: '40px', fontWeight: 'bold', marginLeft: '10px' }}>회원가입</div>
                </div>
                <div style={{ fontSize: '15px', color: 'white', marginTop: '-50px', marginRight: '350px' }}>
                    회원 아이디와 비밀번호를 잊으셨나요?
                </div>
            </div>    
        </div>
    ); 
};

export default MemberJoin;
