import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Detail(props) {
    let { id } = useParams();  // URL에서 id 값을 가져옴
    let 찾은상품 = props.MG.find(x => x.id == id);  // id에 맞는 상품을 찾음

    let [alert, setAlert] = useState(true);

    useEffect(() => {
        let a = setTimeout(() => {
            setAlert(false);
        }, 2000);
        return () => {
            clearTimeout(a);
        };
    }, []);

    return (
        <div className="container">
            {/* 경고 메시지 */}
            {alert && <div className="alert alert-warning">2초 내에 주문 시 할인 적용</div>}
            
            <div className="row">
                <div className="col-md-6">
                    {/* 상품 데이터에서 동적으로 이미지 경로를 가져와 렌더링 */}
                    <img src={찾은상품.image} alt={찾은상품.gundam_name} width="100%" />
                </div>
                <div className="col-md-6">
                    <h4 className="pt-5">{찾은상품.gundam_series}</h4>
                    <p>{찾은상품.gundam_name}</p>
                    <p>{찾은상품.price}</p>
                    <button className="btn btn-danger">주문하기</button>
                </div>
            </div>
        </div>
    );
}

export default Detail;
