import { useParams } from "react-router-dom";
import { Tabs, Tab } from "react-bootstrap";

function Detail({ MG }) {
  let { id } = useParams();
  let 찾은상품 = MG.find(x => x.id == id);

  if (!찾은상품) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="container detail-container">
      <div className="row">
        <div className="col-md-6">
          {/* 첫 번째 상세 이미지 */}
          <img src={찾은상품.detail_images[0]} alt={찾은상품.gundam_name} width="100%" />
        </div>

        <div className="col-md-6">
          <h2>{찾은상품.gundam_name}</h2>
          <p>{찾은상품.price}</p>
          <p>{찾은상품.description}</p>
        </div>
      </div>

      {/* 상세 보기와 리뷰 탭 */}
      <Tabs defaultActiveKey="detail" id="product-tabs" className="mb-3">
        <Tab eventKey="detail" title="상세보기">
          <div>
            {찾은상품.detail_images && 찾은상품.detail_images.length > 0 ? (
              찾은상품.detail_images.map((image, index) => (
                <img key={index} src={image} alt={`상세 이미지 ${index + 1}`} width="100%" />
              ))
            ) : (
              <p>상세 이미지가 없습니다.</p>
            )}
          </div>
        </Tab>
        <Tab eventKey="reviews" title="상품리뷰">
          <p>리뷰가 없습니다.</p>
        </Tab>
      </Tabs>
    </div>
  );
}

export default Detail;
