import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { Tabs, Tab } from 'react-bootstrap';
import StarRating from './StarRating'; // StarRating Ïª¥Ìè¨ÎÑåÌä∏ ÏûÑÌè¨Ìä∏

// Î¶¨Î∑∞ÏóêÏÑú Î≥ÑÏ†ê ÌëúÏãúÏö© Ïª¥Ìè¨ÎÑåÌä∏
function ReviewStars({ rating }) {
  return (
    <div className="review-stars" style={{ display: 'flex', alignItems: 'center' }}>
      {[...Array(5)].map((star, index) => {
        const currentRating = index + 1;
        return (
          <span key={index} className="star" style={{ color: currentRating <= rating ? 'gold' : 'gray', fontSize: '20px' }}>
            ‚òÖ
          </span>
        );
      })}
      {/* Ïà´ÏûêÎ°ú Î≥ÑÏ†êÏùÑ ÌëúÏãú */}
      <span style={{ marginLeft: '10px', fontSize: '20px' }}>{rating}</span>
    </div>
  );
}

function Detail(props) {
  let { id } = useParams();
  let Ï∞æÏùÄÏÉÅÌíà = props.MG.find(x => x.id == id);

  // ÏàòÎüâÏùÑ Í¥ÄÎ¶¨ÌïòÎäî ÏÉÅÌÉú
  let [quantity, setQuantity] = useState(1);

  // Î¶¨Î∑∞ÏôÄ Î≥ÑÏ†êÏùÑ Í¥ÄÎ¶¨ÌïòÎäî ÏÉÅÌÉú
  let [reviews, setReviews] = useState([
    { user: "ÏÇ¨Ïö©Ïûê1", content: "Ï†ïÎßê Î©ãÏßÑ ÌîÑÎùºÎ™®Îç∏ÏûÖÎãàÎã§!", rating: 5, likes: 0, image: null },
    { user: "ÏÇ¨Ïö©Ïûê2", content: "ÏôÑÏÑ±ÎèÑ ÎÜíÏùÄ Ï†úÌíàÏûÖÎãàÎã§.", rating: 4, likes: 2, image: null }
  ]);
  let [newReview, setNewReview] = useState("");
  let [newRating, setNewRating] = useState(0); // ÏÉà Î¶¨Î∑∞Ïóê ÎåÄÌïú Î≥ÑÏ†ê
  let [newImage, setNewImage] = useState(null); // ÏÉà Î¶¨Î∑∞Ïóê ÎåÄÌïú Ïù¥ÎØ∏ÏßÄ

  // Ï¢ãÏïÑÏöî Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ïà´Ïûê Ï¶ùÍ∞Ä
  const handleLike = (index) => {
    const updatedReviews = [...reviews];
    updatedReviews[index].likes += 1;
    setReviews(updatedReviews);
  };

  // Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù Ïãú
  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setNewImage(URL.createObjectURL(e.target.files[0])); // Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ URL ÏÑ§Ï†ï
    }
  };

  // Î¶¨Î∑∞ Ï∂îÍ∞Ä Ìï®Ïàò
  const addReview = () => {
    if (newReview) {
      setReviews([...reviews, { user: "ÏùµÎ™Ö ÏÇ¨Ïö©Ïûê", content: newReview, rating: newRating, likes: 0, image: newImage }]);
      setNewReview("");  // ÏûÖÎ†• ÌïÑÎìúÎ•º ÎπÑÏõÄ
      setNewRating(0);   // Î≥ÑÏ†ê Ï¥àÍ∏∞Ìôî
      setNewImage(null); // Ïù¥ÎØ∏ÏßÄ Ï¥àÍ∏∞Ìôî
    }
  };

  return (
    <div className="container detail-container" style={{ marginTop: '50px' }}>
      <div className="row">
        <div className="col-md-6">
          <img src={process.env.PUBLIC_URL + `/images/mg${id}.jpg`} alt={Ï∞æÏùÄÏÉÅÌíà.gundam_name} width="100%" />
        </div>

        <div className="col-md-6">
          <h2>{Ï∞æÏùÄÏÉÅÌíà.gundam_name}</h2>

          <div className="product-info">
            <p><strong>ÏãúÎ¶¨Ï¶àÎ™Ö: </strong>{Ï∞æÏùÄÏÉÅÌíà.gundam_series}</p>
            <p><strong>ÏàòÎ†π Î∞©Î≤ï: </strong>ÌÉùÎ∞∞ ÏàòÎ†π</p>
          </div>

          <div className="quantity-selector" style={{ marginBottom: '20px' }}>
            <button onClick={() => setQuantity(quantity > 1 ? quantity - 1 : 1)} className="btn btn-outline-secondary">-</button>
            <span className="quantity-box">{quantity}</span>
            <button onClick={() => setQuantity(quantity + 1)} className="btn btn-outline-secondary">+</button>
          </div>

          <p><strong>Ï¥ù ÏÉÅÌíàÍ∏àÏï°: </strong>{parseInt(Ï∞æÏùÄÏÉÅÌíà.price.replace(/[^0-9]/g, '')) * quantity}Ïõê</p>

          <div className="actions" style={{ marginTop: '20px' }}>
            <button className="btn btn-danger" style={{ marginRight: '10px' }}>Íµ¨Îß§ÌïòÍ∏∞</button>
            <button className="btn btn-outline-dark" style={{ marginRight: '10px' }}>Ïû•Î∞îÍµ¨Îãà</button>
            <button className="btn btn-outline-dark">ÏÑ†Î¨ºÌïòÍ∏∞</button>
          </div>
        </div>
      </div>

      {/* ÌÉ≠ ÏÑπÏÖò */}
      <div className="product-tabs mt-5">
        <Tabs defaultActiveKey="detail" id="product-tabs" className="mb-3">
          <Tab eventKey="detail" title="ÏÉÅÏÑ∏Î≥¥Í∏∞">
            {/* ÏÉÅÏÑ∏ Ïù¥ÎØ∏ÏßÄ Î∞è ÏÑ§Î™Ö */}
            <div>
              {Ï∞æÏùÄÏÉÅÌíà.detail_images && Ï∞æÏùÄÏÉÅÌíà.detail_images.length > 0 ? (
                Ï∞æÏùÄÏÉÅÌíà.detail_images.map((image, index) => (
                  <img key={index} src={process.env.PUBLIC_URL + image} alt={`ÏÉÅÏÑ∏ Ïù¥ÎØ∏ÏßÄ ${index + 1}`} width="100%" className="mb-3" />
                ))
              ) : (
                <p>ÏÉÅÏÑ∏ Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
              )}

              <p>{Ï∞æÏùÄÏÉÅÌíà.description}</p>
            </div>
          </Tab>

          <Tab eventKey="reviews" title="ÏÉÅÌíàÎ¶¨Î∑∞">
            <div>
              {/* Î¶¨Î∑∞ Î¶¨Ïä§Ìä∏ */}
              {reviews.map((review, index) => (
                <div key={index} className="review" style={{ display: 'flex', flexDirection: 'column', marginBottom: '20px' }}>
