                {newImage && <img src={newImage} alt="ÎØ∏Î¶¨Î≥¥Í∏∞" style={{ maxWidth: '150px', marginTop: '10px' }} />}
                <button className="btn btn-primary mt-3" onClick={addReview}>Î¶¨Î∑∞ Ï∂îÍ∞Ä</button>
              </div>
            </div>
          </Tab>
        </Tabs>
      </div>
    </div>
  );
}

