import React, { useState } from 'react';
import { useLocation } from 'react-router-dom'; // useLocation으로 state 가져오기
import '../css/Checkout.css';

function Checkout() {
  const location = useLocation();
  const { image, gundam_name, price, quantity } = location.state || {}; // Detail에서 전달받은 state

  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [coupon, setCoupon] = useState('');
  const [totalPrice, setTotalPrice] = useState(30000 + 3000);  // 예시 금액 (상품 금액 + 배송비)

  return (
    <div className="checkout-container">
      <h2>주문/결제</h2>

      {/* 주문리스트 섹션 */}
      <div className="order-list-section">
        <h3>주문리스트</h3>
        <div className="order-item">
          <img src={image} alt={gundam_name} />
          <div className="order-details">
            <p>상품명: {gundam_name}</p>
            <p>수량: {quantity}개</p>
            <p>상품 금액: {parseInt(price.replace(/[^0-9]/g, '')) * quantity}원</p>
          </div>
        </div>
      </div>

      {/* 배송지 정보 입력 섹션 */}
      <div className="address-section">
        <h3>배송지 정보 입력</h3>
        <label>주소</label>
        <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        <label>이메일</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <label>연락처</label>
        <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />
      </div>

      {/* 할인 내역 입력 섹션 */}
      <div className="discount-section">
        <h3>할인 내역 입력</h3>
        <label>쿠폰</label>
        <input type="text" value={coupon} onChange={(e) => setCoupon(e.target.value)} />
        <p>적립금: 0원</p>
      </div>

      {/* 결제 정보 섹션 */}
      <div className="payment-section">
        <h3>결제 정보</h3>
        <p>상품 금액: {parseInt(price.replace(/[^0-9]/g, '')) * quantity}원</p>
        <p>배송비: 3,000원</p>
        <h4>총 결제 금액: {totalPrice}원</h4>
        <button className="btn btn-primary">결제하기</button>
      </div>
    </div>
  );
}

export default Checkout;
