import { useState } from "react"
import { Container, Nav, Navbar } from './react-bootstrap';
import './App.css';
import mg from './data.js';


function App() {

  let [MG] = useState(mg)

  return (
    <div className="App">
      <Navbar bg="dark" data-bs-theme="dark">
        <Container>
          <Navbar.Brand href="#home">GundShop</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#home">로그인</Nav.Link>
            <Nav.Link href="#features">회원가입</Nav.Link>
            <Nav.Link href="#pricing">마이페이지</Nav.Link>
            <Nav.Link href="#pricing">장바구니</Nav.Link>
            <Nav.Link href="#pricing">고객센터</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <div className="main-bg"></div>

      <div className="container">
        <div className="row">
          <Card MG={MG[0]} i={1}></Card> 
          <Card MG={MG[1]} i={2}></Card> 
          <Card MG={MG[2]} i={3}></Card> 
        </div>
      </div> 
    </div>
  );
}

function Card(prop) {
  return (
    <div className="col-md-4">
              <img src={process.env.PUBLIC_URL + '/images/mg.i.jpg'}/>
              <p>{ prop.MG.gundam_series }</p>
              <h5>{ prop.MG.gundam_name}</h5>
              <h4>{ prop.MGprice}</h4>
          </div>     
  );
}

export default App;
