import { useState } from "react"
import { Container, Nav, Navbar } from './react-bootstrap';
import './App.css';
import mg from './data.js';
import { Routes, Route, useNavigate, Outlet } from 'react-router-dom'
import Detail from "./pages/Detail.js";
import Login from "./pages/Login.js";
import MemberSearch from './pages/Member_search';
import MemberJoin from './pages/Member_Join';





function App() {

  let [MG] = useState(mg)
  let navigate = useNavigate();

  return (
    <div className="App">

      <Navbar bg="dark" data-bs-theme="dark">
              <Container>
                <Navbar.Brand href="#home">GundShop</Navbar.Brand>
                <Nav className="me-auto">
                  <Nav.Link onClick={()=>{ navigate('/') }}>홈</Nav.Link>
                  <Nav.Link onClick={()=>{ navigate('/login') }}>로그인</Nav.Link>
                  <Nav.Link onClick={()=>{ navigate('/join') }}>회원가입</Nav.Link>
                  <Nav.Link onClick={()=>{ navigate('/join') }}>메인페이지</Nav.Link>
                  <Nav.Link onClick={()=>{ navigate('/join') }}>건담 프라모델 . 기타</Nav.Link>

                </Nav>
              </Container>
      </Navbar>

      <Routes>
        <Route path="/" element={
          <>
          <div className="main-bg"></div>
          <div className="container">
            <div className="row">
              {/* <Card MG={MG[1]} i={1}></Card> 
              <Card MG={MG[2]} i={2}></Card>  */}
              {
                MG.map((a, i)=>{
                  return (
                    <Card MG={MG[i]} i={i}></Card> 
                  )
                })
              }
            </div>
          </div> 
          </>
        }/>
        <Route path="/detail/:id" element={<Detail MG={MG} />} />

        <Route path="/login" element={<Login/>} />

        <Route path="/search" element={<MemberSearch />} />

        <Route path="/join" element={<MemberJoin />} />

        {/* 404 페이지 (오타시 나오는 화면) */}
        <Route path="*" element={<div>없는페이지 입니다.</div>}/>

        <Route />
      </Routes>
    </div>
  );
}


function Card(props) {
  return (
    <div className="col-md-4">
              <img src={process.env.PUBLIC_URL + '/images/mg' + (props.i+1) + '.jpg'}/>
              <p>{ props.MG.gundam_series }</p>
              <h5>{ props.MG.gundam_name}</h5>
              <h4>{ props.MG.price}</h4>
          </div>     
  );
}

export default App;
