import React from 'react';
import { useLocation } from 'react-router-dom';
import '../css/Checkout.css';

function Checkout() {
  const location = useLocation();
  const { image, gundam_name, totalPrice, quantity } = location.state || {};

  return (
    <div className="checkout-container">
      <h2>주문/결제</h2>

      {/* 주문리스트 섹션 */}
      <div className="order-list-section">
        <h3>주문리스트</h3>
        <div className="order-item">
          <img src={image} alt="상품 이미지" />
          <div className="order-details">
            <p>상품명: {gundam_name}</p>
            <p>수량: {quantity}개</p>
            <p>상품 금액: {totalPrice}원</p>
          </div>
        </div>
      </div>

      {/* 배송지 정보 입력 섹션 */}
      <div className="address-section">
        <h3>배송지 정보 입력</h3>
        <label>주소</label>
        <input type="text" />
        <label>이메일</label>
        <input type="email" />
        <label>연락처</label>
        <input type="tel" />
      </div>

      {/* 할인 내역 입력 섹션 */}
      <div className="discount-section">
        <h3>할인 내역 입력</h3>
        <label>쿠폰</label>
        <input type="text" />
        <p>적립금: 0원</p>
      </div>

      {/* 결제 정보 섹션 */}
      <div className="payment-section">
        <h3>결제 정보</h3>
        <p>상품 금액: {totalPrice}원</p>
        <p>배송비: 3,000원</p>
        <h4>총 결제 금액: {totalPrice + 3000}원</h4>
        <button className="btn btn-primary">결제하기</button>
      </div>
    </div>
  );
}

export default Checkout;
